import { ref, onMounted } from "vue";
import ProductList from "./components/ProductList.vue";
import ProductCart from "./components/ProductCart.vue";

// Cart

export default {
  __name: 'App',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const cart = ref([]);

    // Function to save the cart to localStorage
    const saveCartToLocalStorage = () => {
      localStorage.setItem("cart", JSON.stringify(cart.value));
    };

    // Loading the cart from localStorage on startup
    onMounted(() => {
      const savedCart = localStorage.getItem("cart");
      if (savedCart) {
        cart.value = JSON.parse(savedCart);
      }
    });

    // Adding a product to the cart based on quantity
    const addToCart = productWithQty => {
      const existingItem = cart.value.find(item => item.id === productWithQty.id);
      if (existingItem) {
        existingItem.quantity += productWithQty.quantity;
      } else {
        cart.value.push({
          ...productWithQty
        });
      }
      saveCartToLocalStorage();
    };

    // Change product quantity
    const increaseQty = item => {
      item.quantity += 1;
      saveCartToLocalStorage();
    };
    const decreaseQuantity = item => {
      if (item.quantity > 1) {
        item.quantity -= 1;
      } else {
        removeFromCart(item);
      }
      saveCartToLocalStorage();
    };

    // Remove an item from the cart
    const removeFromCart = item => {
      cart.value = cart.value.filter(cartItem => cartItem.id !== item.id);
      saveCartToLocalStorage();
    };
    const clearCart = () => {
      cart.value = [];
      saveCartToLocalStorage();
    };
    const __returned__ = {
      cart,
      saveCartToLocalStorage,
      addToCart,
      increaseQty,
      decreaseQuantity,
      removeFromCart,
      clearCart,
