import { ref, onMounted } from "vue";
import ProductList from "./components/ProductList.vue";
import ProductCart from "./components/ProductCart.vue";

// Кошик

export default {
  __name: 'App',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const cart = ref([]);

    // Функція для збереження кошика в localStorage
    const saveCartToLocalStorage = () => {
      localStorage.setItem("cart", JSON.stringify(cart.value));
    };

    // Завантаження кошика з localStorage при старті
    onMounted(() => {
      const savedCart = localStorage.getItem("cart");
      if (savedCart) {
        cart.value = JSON.parse(savedCart);
      }
    });

    // Додавання товару до кошика з урахуванням кількості
    const addToCart = productWithQty => {
      const existingItem = cart.value.find(item => item.id === productWithQty.id);
      if (existingItem) {
        existingItem.quantity += productWithQty.quantity;
      } else {
        cart.value.push({
          ...productWithQty
        });
      }
      saveCartToLocalStorage(); // Зберігаємо після додавання
    };

    // Зміна кількості товару
    const increaseQuantity = item => {
      item.quantity += 1;
      saveCartToLocalStorage(); // Зберігаємо після зміни
    };
    const decreaseQuantity = item => {
      if (item.quantity > 1) {
        item.quantity -= 1;
      } else {
        removeFromCart(item);
      }
      saveCartToLocalStorage(); // Зберігаємо після зміни
    };

    // Видалення товару з кошика
    const removeFromCart = item => {
      cart.value = cart.value.filter(cartItem => cartItem.id !== item.id);
      saveCartToLocalStorage(); // Зберігаємо після видалення
    };
    const __returned__ = {
      cart,
      saveCartToLocalStorage,
      addToCart,
      increaseQuantity,
      decreaseQuantity,
      removeFromCart,
      ref,
      onMounted,
      ProductList,
      ProductCart
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
